import { Component,OnInit } from '@angular/core';
import { FormGroup,  FormBuilder,  Validators } from '@angular/forms';

@Component({
  selector: 'app-student',
  templateUrl: './student.component.html',
  styleUrls: ['./student.component.css']
})
export class StudentComponent  {

  userForm :FormGroup;
  studentData: any;


  constructor(private fb: FormBuilder){

    this.studentData=[];

    this.userForm = this.fb.group({
      name :['',Validators.required],
      id :['',Validators.required],
      course :['',Validators.required],
      number :['',Validators.required]
    })
  }

  addItem(){
    //add validations here

          this.studentData.push(this.userForm.value);
  }

  reset(){
        this.userForm.reset();
  }



}
<form class="form"  [formGroup]="userForm">
<div class="custom">
Student Name
<input type="text" placeholder="Enter Name" formControlName="name" >
<br>
Student Id
<input type="text" placeholder="Enter Id" formControlName="id">
<br>
Student Course
<input type="text" placeholder="Enter Course" formControlName="course">
<br>
Student Number
<input type="text" placeholder="Enter Number" formControlName="number">
<br><button (click)="addItem()">Add</button>
<button (click)="reset()">Reset</button>
</div> 
</form>

<div class="tbl">
<table>
    <tr>
        <th>Student Name</th>
        <th>Student Id</th>
        <th>Student Course</th>
        <th>Student Number</th>

    </tr>
    <tbody>
    <tr *ngFor="let student of studentData">
        <td>{{student.name}}</td>
        <td>{{student.id}}</td>
        <td>{{student.course}}</td>
        <td>{{student.number}}</td>

    </tr>
</tbody>
</table>
</div>
